#!/usr/bin/env python3

# Assign the nucleotide sequence to a variable
DNA = ""

# Determine the set of unique characters found in the DNA variable
uniq_nts = set(DNA)

# Create an empty dictionary to append the nucleotide composition
nt_count = {}

# Iterate through each one of the characters in the DNA variable and count for the unique characters found in the uniq_nts variable
for nt in uniq_nts:
    count = DNA.count(nt)
    nt_count[nt] = count

# Extract the sum of nucleotides
print(
"Nucleotide composition:", nt_count,
"\nGC content:", round(((nt_count["G"] + nt_count["C"])/sum(nt_count.values()))*100, 2)
)
